import React, { useState } from 'react';

const AddProductForm = ({ onProductAdded }) => {
  const [product, setProduct] = useState({
    name: '',
    price: '',
    category: '',
    description: '',
    image: null,
  });

  const [descriptionTemplate, setDescriptionTemplate] = useState('');

  const handleInputChange = e => {
    const { name, value } = e.target;
    setProduct(prev => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleDescriptionTemplateChange = e => {
    const { value } = e.target;
    setDescriptionTemplate(value);
    setProduct(prev => ({
      ...prev,
      description: value,
    }));
  };

  const handleImageChange = e => {
    setProduct(prev => ({
      ...prev,
      image: e.target.files[0],
    }));
  };

  const handleSubmit = e => {
    e.preventDefault();
    // Додавання нового продукту через API
    onProductAdded(product);
    setProduct({
      name: '',
      price: '',
      category: '',
      description: '',
      image: null,
    });
  };

  return (
    <form onSubmit={handleSubmit}>
      <h3>Add New Product</h3>
      <div>
        <label>Name:</label>
        <input
          type="text"
          name="name"
          value={product.name}
          onChange={handleInputChange}
        />
      </div>
      <div>
        <label>Price:</label>
        <input
          type="number"
          name="price"
          value={product.price}
          onChange={handleInputChange}
        />
      </div>
      <div>
        <label>Category:</label>
        <select
          name="category"
          value={product.category}
          onChange={handleInputChange}
        >
          <option value="">Select Category</option>
          <option value="Building Materials">Building Materials</option>
          <option value="Furniture">Furniture</option>
          <option value="Electronics">Electronics</option>
        </select>
      </div>
      <div>
        <label>Description:</label>
        <select
          value={descriptionTemplate}
          onChange={handleDescriptionTemplateChange}
        >
          <option value="">Select Description Template</option>
          <option value="This product is perfect for your home, easy to apply and has high quality.">
            Standard Home Product Description
          </option>
          <option value="This cement is designed for construction and repair, with high durability and strength.">
            Cement Description
          </option>
          <option value="This furniture is durable, elegant, and perfect for any modern living space.">
            Furniture Description
          </option>
        </select>
      </div>
      <div>
        <label>Image:</label>
        <input type="file" onChange={handleImageChange} />
      </div>
      <button type="submit">Add Product</button>
    </form>
  );
};

export default AddProductForm;
