import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import ProductList from './components/ProductList';
import AddProductForm from './components/AddProductForm';
import EditProductForm from './components/EditProductForm'; // новий компонент для редагування

const App = () => {
  const [products, setProducts] = useState([]); // Для оновлення списку продуктів

  const handleProductAdded = newProduct => {
    setProducts(prevProducts => [...prevProducts, newProduct]);
  };

  const handleProductUpdated = updatedProduct => {
    setProducts(prevProducts =>
      prevProducts.map(product =>
        product._id === updatedProduct._id ? updatedProduct : product
      )
    );
  };

  const handleProductDeleted = id => {
    setProducts(prevProducts =>
      prevProducts.filter(product => product._id !== id)
    );
  };

  return (
    <Router>
      <nav style={{ marginBottom: '20px' }}>
        <Link to="/" style={{ marginRight: '10px' }}>
          Product List
        </Link>
        <Link to="/add-product">Add Product</Link>
      </nav>
      <Routes>
        <Route
          path="/"
          element={
            <ProductList
              products={products}
              setProducts={setProducts}
              onProductDeleted={handleProductDeleted}
              onProductUpdated={handleProductUpdated}
            />
          }
        />
        <Route
          path="/add-product"
          element={<AddProductForm onProductAdded={handleProductAdded} />}
        />
        <Route
          path="/edit-product/:id"
          element={<EditProductForm onProductUpdated={handleProductUpdated} />}
        />
      </Routes>
    </Router>
  );
};

export default App;
