import React, { useState } from 'react';
import { addProduct } from '../utils/api';

const AddProductForm = ({ onProductAdded }) => {
  const [formData, setFormData] = useState({
    name: '',
    price: '',
    category: '',
    description: '',
    isPromotional: false,
    image: null,
  });

  const handleChange = e => {
    const { name, value } = e.target;
    setFormData(prevState => ({
      ...prevState,
      [name]: value,
    }));
  };

  const handleImageChange = e => {
    setFormData(prevState => ({
      ...prevState,
      image: e.target.files[0],
    }));
  };

  const handleSubmit = async e => {
    e.preventDefault();
    const form = new FormData();
    form.append('name', formData.name);
    form.append('price', formData.price);
    form.append('category', formData.category);
    form.append('description', formData.description);
    form.append('isPromotional', formData.isPromotional);
    form.append('image', formData.image);

    try {
      const newProduct = await addProduct(form);
      onProductAdded(newProduct); // Додаємо новий продукт у список
    } catch (error) {
      console.error('Error adding product:', error);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input
        type="text"
        name="name"
        value={formData.name}
        onChange={handleChange}
        placeholder="Name"
        required
      />
      <input
        type="number"
        name="price"
        value={formData.price}
        onChange={handleChange}
        placeholder="Price"
        required
      />
      <input
        type="text"
        name="category"
        value={formData.category}
        onChange={handleChange}
        placeholder="Category"
        required
      />
      <textarea
        name="description"
        value={formData.description}
        onChange={handleChange}
        placeholder="Description"
        required
      />
      <label>
        Promotional
        <input
          type="checkbox"
          name="isPromotional"
          checked={formData.isPromotional}
          onChange={e =>
            setFormData({ ...formData, isPromotional: e.target.checked })
          }
        />
      </label>
      <input
        type="file"
        name="image"
        onChange={handleImageChange}
        accept="image/*"
      />
      <button type="submit">Add Product</button>
    </form>
  );
};

export default AddProductForm;
